import React, { useRef, useEffect, useState, useCallback } from 'react';
import './VideoCapture.css';

const VideoCapture = ({ isActive, onImageCapture }) => {
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const [isCameraReady, setIsCameraReady] = useState(false);
  const [cameraError, setCameraError] = useState(false);

  // Function to capture image from video
  const captureImage = useCallback(() => {
    if (videoRef.current && videoRef.current.videoWidth > 0) {
      console.log('Capturing image from video');
      try {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

        const imageDataUrl = canvas.toDataURL('image/jpeg');
        console.log('Image captured successfully, data URL length:', imageDataUrl.length);
        onImageCapture(imageDataUrl);
        return true;
      } catch (error) {
        console.error('Error capturing image:', error);
        return false;
      }
    } else {
      console.warn('Video element not ready for capture');
      return false;
    }
  }, [onImageCapture]);

  // Periodic image capture during recording
  useEffect(() => {
    let captureInterval;

    if (isActive && isCameraReady) {
      // Capture an initial image after camera is ready
      setTimeout(() => {
        captureImage();
      }, 1000);

      // Set up interval to capture image every 5 seconds during recording
      captureInterval = setInterval(() => {
        captureImage();
      }, 5000);
    }

    return () => {
      if (captureInterval) {
        clearInterval(captureInterval);
      }
    };
  }, [isActive, isCameraReady, captureImage]);

  // Handle camera setup and teardown
  useEffect(() => {
    if (isActive && !isCameraReady && !cameraError) {
      console.log('Starting camera');
      // Start camera
      navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: { ideal: 1280 },
          height: { ideal: 720 }
        } 
      })
        .then(stream => {
          console.log('Camera access granted');
          streamRef.current = stream;
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
            videoRef.current.onloadedmetadata = () => {
              videoRef.current.play()
                .then(() => {
                  console.log('Video playback started');
                  setIsCameraReady(true);
                })
                .catch(error => {
                  console.error('Error starting video playback:', error);
                  setCameraError(true);
                });
            };
          }
        })
        .catch(error => {
          console.error('Error accessing camera:', error);
          setCameraError(true);
        });
    } else if (!isActive) {
      // Stop camera and capture final image if we were active
      if (streamRef.current && isCameraReady) {
        console.log('Stopping camera and capturing final image');
        const captured = captureImage();

        // Ensure we have at least one image
        if (!captured) {
          console.log('Final capture failed, trying one more time');
          setTimeout(() => {
            captureImage();
          }, 500);
        }

        // Stop all tracks
        streamRef.current.getTracks().forEach(track => {
          track.stop();
          console.log(`Track ${track.kind} stopped`);
        });
        streamRef.current = null;
        setIsCameraReady(false);
      }
    }

    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, [isActive, isCameraReady, captureImage, cameraError]);

  // Manual capture button handler
  const handleManualCapture = () => {
    if (isCameraReady) {
      captureImage();
    }
  };

  return (
    <div className="video-capture">
      <video 
        ref={videoRef} 
        className={isActive ? 'active' : 'inactive'} 
        muted
        playsInline
      />

      {isActive && isCameraReady && (
        <button 
          className="capture-button" 
          onClick={handleManualCapture}
          title="Take a snapshot"
        >
          📷
        </button>
      )}

      {!isActive && !isCameraReady && !cameraError && (
        <div className="camera-inactive">
          <p>Click "Start Stream" to activate camera</p>
        </div>
      )}

      {cameraError && (
        <div className="camera-error">
          <p>Camera access error. Please check your permissions.</p>
        </div>
      )}
    </div>
  );
};

export default VideoCapture;
