import React, { useRef, useEffect, useState, useCallback } from 'react';
import './VideoCapture.css';

const VideoCapture = ({ isActive, onImageCapture }) => {
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const canvasRef = useRef(document.createElement('canvas'));
  const [isCameraReady, setIsCameraReady] = useState(false);
  const [cameraError, setCameraError] = useState(false);
  const [hasValidImage, setHasValidImage] = useState(false);
  const [cameraStatus, setCameraStatus] = useState('Initializing camera...');

  // Function to check if an image is blank/black
  const isImageBlank = (imageData) => {
    // Create a temporary canvas to analyze the image
    const tempCanvas = document.createElement('canvas');
    const tempCtx = tempCanvas.getContext('2d');
    const img = new Image();

    // Set up a promise to handle the async image loading
    return new Promise((resolve) => {
      img.onload = () => {
        tempCanvas.width = img.width;
        tempCanvas.height = img.height;
        tempCtx.drawImage(img, 0, 0);

        // Get image data and check if it's mostly black
        const data = tempCtx.getImageData(0, 0, tempCanvas.width, tempCanvas.height).data;
        let nonBlackPixels = 0;
        const totalPixels = data.length / 4;

        // Sample pixels to check for non-black content
        for (let i = 0; i < data.length; i += 16) {
          const r = data[i];
          const g = data[i + 1];
          const b = data[i + 2];

          // If pixel is not black (allowing some noise)
          if (r > 10 || g > 10 || b > 10) {
            nonBlackPixels++;
          }
        }

        // If less than 5% of sampled pixels are non-black, consider it blank
        const isBlank = (nonBlackPixels / (totalPixels / 4)) < 0.05;
        console.log(`Image analysis: ${nonBlackPixels} non-black pixels out of ${totalPixels / 4} sampled. Blank: ${isBlank}`);
        resolve(isBlank);
      };

      img.onerror = () => {
        console.error('Error analyzing image');
        resolve(true); // Assume blank on error
      };

      img.src = imageData;
    });
  };

  // Function to capture image from video
  const captureImage = useCallback(async () => {
    if (!videoRef.current) {
      console.warn('Video element not available');
      return false;
    }

    if (!isCameraReady) {
      console.warn('Camera not ready yet');
      return false;
    }

    if (videoRef.current.videoWidth <= 0 || videoRef.current.videoHeight <= 0) {
      console.warn('Video dimensions not available yet');
      return false;
    }

    console.log('Capturing image from video', {
      videoWidth: videoRef.current.videoWidth,
      videoHeight: videoRef.current.videoHeight,
      readyState: videoRef.current.readyState
    });

    try {
      // Force a draw to make sure there's a frame to capture
      if (videoRef.current.readyState < 2) {
        console.warn('Video not fully loaded, waiting...');
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      // Ensure canvas dimensions match video
      canvasRef.current.width = videoRef.current.videoWidth || 640;
      canvasRef.current.height = videoRef.current.videoHeight || 480;

      // Draw the current video frame to canvas
      const ctx = canvasRef.current.getContext('2d');
      ctx.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);

      // Convert to data URL with higher quality
      const imageDataUrl = canvasRef.current.toDataURL('image/jpeg', 0.95);

      console.log('Raw image captured, data URL length:', imageDataUrl.length);

      // Check if the image is blank
      const isBlank = await isImageBlank(imageDataUrl);

      if (isBlank) {
        console.warn('Captured image appears to be blank');
        return false;
      }

      console.log('Valid image captured successfully, data URL length:', imageDataUrl.length);

      // Add a small delay to ensure the state updates properly
      setTimeout(() => {
        onImageCapture(imageDataUrl);
        setHasValidImage(true);
      }, 100);

      return true;
    } catch (error) {
      console.error('Error capturing image:', error);
      return false;
    }
  }, [onImageCapture, isCameraReady]);

  // Create a dedicated capture function that users can call
  const forceCapture = async () => {
    setCameraStatus('Capturing image...');
    const success = await captureImage();
    setCameraStatus(success ? 'Image captured!' : 'Failed to capture image');

    // Reset status after a delay
    setTimeout(() => {
      setCameraStatus(isCameraReady ? 'Camera ready' : 'Initializing camera...');
    }, 2000);
  };

  // Periodic image capture during recording
  useEffect(() => {
    let captureInterval;

    if (isActive && isCameraReady) {
      // Wait a bit longer before first capture to ensure video is rendering
      const initialCaptureDelay = 3000;
      console.log(`Scheduling initial capture in ${initialCaptureDelay}ms`);

      const initialCaptureTimeout = setTimeout(async () => {
        const success = await captureImage();
        if (!success) {
          console.log('Initial capture failed, trying again in 2 seconds');
          setTimeout(captureImage, 2000);
        }
      }, initialCaptureDelay);

      // Set up interval to capture image every 5 seconds during recording if no valid image yet
      captureInterval = setInterval(async () => {
        if (!hasValidImage) {
          console.log('Attempting periodic capture');
          await captureImage();
        }
      }, 5000);

      return () => {
        clearTimeout(initialCaptureTimeout);
        if (captureInterval) {
          clearInterval(captureInterval);
        }
      };
    }

    return () => {
      if (captureInterval) {
        clearInterval(captureInterval);
      }
    };
  }, [isActive, isCameraReady, captureImage, hasValidImage]);

  // Handle camera setup and teardown
  useEffect(() => {
    if (isActive && !isCameraReady && !cameraError) {
      console.log('Starting camera');
      setHasValidImage(false);
      setCameraStatus('Initializing camera...');

      // Start camera with specific constraints for better quality
      navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: { ideal: 1280 },
          height: { ideal: 720 },
          facingMode: 'user'
        } 
      })
        .then(stream => {
          console.log('Camera access granted');
          streamRef.current = stream;

          if (videoRef.current) {
            // Set srcObject to the stream
            videoRef.current.srcObject = stream;

            // Listen for loadedmetadata event
            videoRef.current.onloadedmetadata = () => {
              console.log('Video metadata loaded');

              // Play the video
              videoRef.current.play()
                .then(() => {
                  console.log('Video playback started');
                  setCameraStatus('Camera ready');

                  // Wait a moment to ensure video is actually rendering frames
                  setTimeout(() => {
                    setIsCameraReady(true);
                  }, 1500);
                })
                .catch(error => {
                  console.error('Error starting video playback:', error);
                  setCameraError(true);
                  setCameraStatus('Camera error: ' + error.message);
                });
            };

            // Listen for errors
            videoRef.current.onerror = (error) => {
              console.error('Video element error:', error);
              setCameraError(true);
              setCameraStatus('Video error: ' + (error.message || 'Unknown error'));
            };
          }
        })
        .catch(error => {
          console.error('Error accessing camera:', error);
          setCameraError(true);
          setCameraStatus('Camera access error: ' + error.message);
        });
    } else if (!isActive) {
      // Stop camera and capture final image if we were active
      if (streamRef.current && isCameraReady) {
        console.log('Stopping camera and capturing final image');
        setCameraStatus('Capturing final image...');

        // Try to capture one last image before stopping
        const captureFinalImage = async () => {
          // Try up to 3 times to get a non-blank image
          let captured = false;
          for (let i = 0; i < 3 && !captured; i++) {
            console.log(`Final capture attempt ${i + 1}/3`);
            const success = await captureImage();
            if (success) {
              console.log('Final capture successful');
              captured = true;
            } else if (i < 2) {
              // Short delay between attempts
              await new Promise(resolve => setTimeout(resolve, 500));
            }
          }

          // Stop all tracks
          streamRef.current.getTracks().forEach(track => {
            track.stop();
            console.log(`Track ${track.kind} stopped`);
          });
          streamRef.current = null;
          setIsCameraReady(false);
          setCameraStatus('Camera stopped');
        };

        captureFinalImage();
      }
    }

    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, [isActive, isCameraReady, captureImage, cameraError]);

  // Manual capture button handler
  const handleManualCapture = async () => {
    if (isCameraReady) {
      console.log('Manual capture triggered');
      await forceCapture();
    }
  };

  return (
    <div className="video-capture">
      <video 
        ref={videoRef} 
        className={isActive ? 'active' : 'inactive'} 
        muted
        playsInline
        autoPlay
      />

      {isActive && (
        <div className="camera-controls">
          <div className="status-indicator">
            {cameraStatus}
            {hasValidImage && <span className="checkmark">✓</span>}
          </div>

          <button 
            className="capture-button"
            onClick={handleManualCapture}
            disabled={!isCameraReady}
            title="Take a snapshot"
          >
            📷 Capture Image Now
          </button>
        </div>
      )}

      {!isActive && !isCameraReady && !cameraError && (
        <div className="camera-inactive">
          <p>Click "Start Stream" to activate camera</p>
        </div>
      )}

      {cameraError && (
        <div className="camera-error">
          <p>{cameraStatus || 'Camera access error. Please check your permissions.'}</p>
        </div>
      )}
    </div>
  );
};

export default VideoCapture;
