import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import AudioRecorder from './components/AudioRecorder';
import VideoCapture from './components/VideoCapture';
import ResultDisplay from './components/ResultDisplay';

const API_URL = 'http://localhost:8000/api';

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [audioText, setAudioText] = useState('');
  const [capturedImage, setCapturedImage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleStartStream = () => {
    setIsRecording(true);
    setAudioText('');
    setCapturedImage(null);
    setResult(null);
    setError(null);
  };

  const handleEndStream = async () => {
    setIsRecording(false);
    setIsProcessing(true);

    try {
      // Send data to backend
      const response = await axios.post(`${API_URL}/process/`, {
        text: audioText,
        image: capturedImage
      });

      setResult(response.data);
    } catch (err) {
      console.error('Error processing data:', err);
      setError(err.response?.data?.error || 'An error occurred while processing your data');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="app-container">
      <h1>Audio & Vision Processor</h1>

      <div className="controls">
        <button 
          className={`control-button ${isRecording ? 'disabled' : 'start'}`}
          onClick={handleStartStream}
          disabled={isRecording || isProcessing}
        >
          Start Stream
        </button>
        <button 
          className={`control-button ${!isRecording ? 'disabled' : 'stop'}`}
          onClick={handleEndStream}
          disabled={!isRecording || isProcessing}
        >
          End Stream
        </button>
      </div>

      <div className="input-container">
        <div className="input-section">
          <h2>Audio Input</h2>
          <AudioRecorder 
            isRecording={isRecording} 
            onTranscriptUpdate={setAudioText} 
          />
          {audioText && (
            <div className="transcript">
              <h3>Transcript:</h3>
              <p>{audioText}</p>
            </div>
          )}
        </div>

        <div className="input-section">
          <h2>Video Input</h2>
          <VideoCapture 
            isActive={isRecording} 
            onImageCapture={setCapturedImage} 
          />
          {capturedImage && (
            <div className="image-preview">
              <h3>Captured Image:</h3>
              <img src={capturedImage} alt="Captured" />
            </div>
          )}
        </div>
      </div>

      {isProcessing && (
        <div className="processing">
          <p>Processing your data...</p>
          <div className="spinner"></div>
        </div>
      )}

      {error && (
        <div className="error">
          <h3>Error:</h3>
          <p>{error}</p>
        </div>
      )}

      {result && <ResultDisplay result={result} />}
    </div>
  );
}

export default App;
