import React, { useRef, useEffect, useState } from 'react';
import './VideoCapture.css';

const VideoCapture = ({ isActive, onImageCapture }) => {
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const [isCameraReady, setIsCameraReady] = useState(false);

  useEffect(() => {
    if (isActive) {
      // Start camera
      navigator.mediaDevices.getUserMedia({ video: true })
        .then(stream => {
          streamRef.current = stream;
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
            videoRef.current.onloadedmetadata = () => {
              videoRef.current.play();
              setIsCameraReady(true);
            };
          }
        })
        .catch(error => {
          console.error('Error accessing camera:', error);
        });
    } else {
      // Stop camera and capture image if we were active
      if (streamRef.current && isCameraReady) {
        captureImage();
        streamRef.current.getTracks().forEach(track => track.stop());
        streamRef.current = null;
        setIsCameraReady(false);
      }
    }

    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, [isActive]);

  const captureImage = () => {
    if (videoRef.current) {
      const canvas = document.createElement('canvas');
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;
      const ctx = canvas.getContext('2d');
      ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

      const imageDataUrl = canvas.toDataURL('image/jpeg');
      onImageCapture(imageDataUrl);
    }
  };

  return (
    <div className="video-capture">
      <video 
        ref={videoRef} 
        className={isActive ? 'active' : 'inactive'} 
        muted
      />

      {!isActive && !isCameraReady && (
        <div className="camera-inactive">
          <p>Click "Start Stream" to activate camera</p>
        </div>
      )}
    </div>
  );
};

export default VideoCapture;
